statements.update:
MERGE /* DH.UPDATE.DAILYWORK.U01 */ INTO DAILYWORK USING DUAL ON ( WK_STF_ID = #WK_STF_ID# AND WK_DT = TO_DATE(#WK_DT#, 'YYYY-MM-DD') AND WK_STR_DTM = TO_DATE(#WK_DT# || ' ' || #WK_STR_DTM#, 'YYYY-MM-DD HH24:MI') AND WK_END_DTM = TO_DATE(#WK_DT# || ' ' || #WK_END_DTM#, 'YYYY-MM-DD HH24:MI')
) WHEN MATCHED THEN UPDATE SET WK_STF_NM = #WK_STF_NM# /*직원명*/ , HDY_GBN = #HDY_GBN# /*휴가구분 1휴가*/ , WK_GBN1 = #WK_GBN1# /*업무구분 문의*/ , WK_GBN2 = #WK_GBN2# /*업무구분 개발*/ , WK_GBN3 = #WK_GBN3# /*업무구분 데이터*/ , WK_GBN4 = #WK_GBN4# /*업무구분 미팅*/ , WK_GBN5 = #WK_GBN5# /**/ , WK_GBN6 = #WK_GBN6# /**/ , WK_GBN7 = #WK_GBN7# /**/ , WK_GBN8 = #WK_GBN8# /**/ , WK_GBN9 = #WK_GBN9# /*업무구분 기타*/ , WK_CNTE = #WK_CNTE# /*업무내용*/ , WK_RMK_CNTE = #WK_RMK_CNTE# /*업무비고내용*/
, LSH_DTM = SYSDATE /*최종변경일시*/ , LSH_STF_NO = #WK_STF_ID# /*최종변경직원번호*/ , LSH_PRGM_NM = 'DH' /*최종변경프로그램명*/ , LSH_IP_ADDR = '0.0.0.0' /*최종변경IP주소*/ WHEN NOT MATCHED THEN INSERT ( WK_STF_ID /*직원ID*/ , WK_STF_NM /*직원명*/ , WK_DT /*업무일자*/ , WK_STR_DTM /*업무시작일시*/ , WK_END_DTM /*업무종료일시*/ , HDY_GBN /*휴가구분 1휴가*/ , WK_GBN1 /*업무구분 문의*/ , WK_GBN2 /*업무구분 개발*/ , WK_GBN3 /*업무구분 데이터*/ , WK_GBN4 /*업무구분 미팅*/ , WK_GBN5 /**/ , WK_GBN6 /**/ , WK_GBN7 /**/ , WK_GBN8 /**/ , WK_GBN9 /*업무구분 기타*/ , WK_CNTE /*업무내용*/ , WK_RMK_CNTE /*업무비고내용*/ , FSR_DTM /*최초등록일시*/ , FSR_STF_NO /*최초등록직원번호*/ , FSR_PRGM_NM /*최초등록프로그램명*/ , FSR_IP_ADDR /*최초등록IP주소*/ , LSH_DTM /*최종변경일시*/ , LSH_STF_NO /*최종변경직원번호*/ , LSH_PRGM_NM /*최종변경프로그램명*/ , LSH_IP_ADDR /*최종변경IP주소*/ ) VALUES ( #WK_STF_ID# , #WK_STF_NM# , TO_DATE(#WK_DT#, 'YYYY-MM-DD') , TO_DATE(#WK_DT# || ' ' || #WK_STR_DTM#, 'YYYY-MM-DD HH24:MI') , TO_DATE(#WK_DT# || ' ' || #WK_END_DTM#, 'YYYY-MM-DD HH24:MI') , #HDY_GBN# , #WK_GBN1# , #WK_GBN2# , #WK_GBN3# , #WK_GBN4# , #WK_GBN5# , #WK_GBN6# , #WK_GBN7# , #WK_GBN8# , #WK_GBN9# , #WK_CNTE# , #WK_RMK_CNTE# , SYSDATE , #WK_STF_ID# , 'DH' , '0.0.0.0' , SYSDATE , #WK_STF_ID# , 'DH' , '0.0.0.0' )
